import React, { useState} from 'react';

import './ExpensesFilter.css';

const ExpensesFilter = () => {

    const [selectedValue, setSelectedValue] = useState('');

    function titleChangeHandler(value)
    {
        setSelectedValue(value);
        console.log(value);
    };

  return (
    <div className='expenses-filter'>
      <div className='expenses-filter__control'>
        <label>Filter by year</label>
        <select>
          <option onChange={titleChangeHandler} value='2022'>2022</option>
          <option onChange={titleChangeHandler} value='2021'>2021 </option>
          <option onChange={titleChangeHandler} value='2020'>2020 </option>
          <option onChange={titleChangeHandler} value='2019'>2019 </option>
        </select>
      </div>
    </div>
  );
};

export default ExpensesFilter;
